import React, { FC, useState, useEffect } from "react";
import HighOrderComp from "../HOC/HighOrderComp";
import mockImage from "../../assets/images/mockImage.jpg";
import { Link } from "react-router-dom";
import "../../styles/newsBody.scss";

interface AllNewsProps {
  data: ArticleProps[];
}

interface ArticleProps {
  title: string;
  url: string;
  urlToImage: string | undefined;
  description: string;
  publishedAt: string;
}

const AllNews: FC<AllNewsProps> = React.memo(({ data }: AllNewsProps) => {
  // const getSortedData = () => {
  //   const storage = localStorage.getItem("sorted");
  //   if (!storage) {
  //     return [...data];
  //   }
  //   return JSON.parse(storage);
  // };

  const [initialData, setInitialData] = useState<ArticleProps[]>(data);
  const [order, setOrder] = useState<string>("asc");

  const parseTime = (time: string) => {
    const parsedDate = new Date(time).toLocaleDateString();
    const parsedTime = new Date(time).toLocaleTimeString();
    return `${parsedDate} ${parsedTime}`;
  };
  const sortByDate = () => {
    let sorted = [...initialData].sort((a, b): number => {
      if (order === "asc") {
        return new Date(a.publishedAt) > new Date(b.publishedAt) ? 1 : -1;
      } else {
        return new Date(a.publishedAt) > new Date(b.publishedAt) ? -1 : 1;
      }
    });
    setOrder((prev: string) => (prev === "asc" ? "desc" : "asc"));
    console.log("sorted", sorted);
    setInitialData(sorted);
  };
  console.log("data", data);

  useEffect(async () => {
    localStorage.setItem("sorted", JSON.stringify(initialData));
  }, [initialData]);

  return (
    <>
      <div>
        <div className="sort-btn">
          <button className="btn-sort" onClick={sortByDate}>
            Order news Asc or Desc
          </button>
        </div>
        {initialData.slice(1, 2).map((item: ArticleProps) => {
          return (
            <div key={item.publishedAt} className="header-section">
              <div className="header-left">
                <h3 className="title">{item.title}</h3>
                <div>
                  <Link className="category-btn" to={item.url}>
                    Kategorija
                  </Link>
                  <Link className="category-btn" to={item.url}>
                    Vrijeme čitanja 5 min
                  </Link>
                  <p className="time">
                    Published: {parseTime(item.publishedAt)}
                  </p>
                </div>
              </div>
              <div className="header-right">
                {item.urlToImage !== undefined || item.urlToImage !== null ? (
                  <img
                    width={650}
                    height={400}
                    loading="lazy"
                    src={item.urlToImage}
                    alt={item.description}
                  />
                ) : (
                  <img
                    width={650}
                    height={400}
                    loading="lazy"
                    src={mockImage}
                    alt={item.description}
                  />
                )}
              </div>
            </div>
          );
        })}
        <hr />
      </div>
      <div className="middle-section">
        {initialData.slice(2, 5).map((item: ArticleProps) => {
          return (
            <div className="card" key={item.publishedAt}>
              {item.urlToImage === undefined || item.urlToImage === null ? (
                <img
                  width={360}
                  height={222}
                  loading="lazy"
                  src={mockImage}
                  alt={item.description}
                />
              ) : (
                <img
                  width={360}
                  height={222}
                  loading="lazy"
                  src={item.urlToImage}
                  alt={item.description}
                />
              )}
              <p className="time">Published: {parseTime(item.publishedAt)}</p>
              <h3 className="title">{item.title}</h3>
              <Link className="category-btn" to={item.url}>
                Enter
              </Link>
            </div>
          );
        })}
      </div>
      <hr />
      <div className="bottom-section">
        <div className="column">
          {initialData.slice(5, 10).map((item: ArticleProps) => {
            return (
              <div className="card-bottom" key={item.publishedAt}>
                <div>
                  {item.urlToImage === undefined || item.urlToImage === null ? (
                    <img
                      width={200}
                      height={122}
                      loading="lazy"
                      src={mockImage}
                      alt={item.description}
                    />
                  ) : (
                    <img
                      width={200}
                      height={122}
                      loading="lazy"
                      src={item.urlToImage}
                      alt={item.description}
                    />
                  )}
                </div>
                <div className="right-side">
                  <p className="time">
                    Published: {parseTime(item.publishedAt)}
                  </p>
                  <h3 className="title">{item.title}</h3>
                  <Link className="category-btn" to={item.url}>
                    Enter
                  </Link>
                </div>
              </div>
            );
          })}
        </div>
        <div>
          <p>Left side</p>
        </div>
      </div>
      <hr className="rule" />
      {/* 2 div */}
    </>
  );
});

const url: string = `http://localhost:5000/api/everything`;

const AllNewsComponent: any = HighOrderComp(AllNews, url);

export default AllNewsComponent;
