import { FC } from "react";
import "../../styles/header.scss";
import logo from "../../assets/images/logo.svg";
import { NavLink } from "react-router-dom";

interface HeaderProps {}

interface headerLinksProps {
  id: string;
  name: string;
  route: string;
}

const headerLinks: headerLinksProps[] = [
  { id: "1", name: "Početna", route: "/" },
  { id: "2", name: "Kalendar", route: "/home" },
  { id: "3", name: "O name", route: "/bus" },
];

const Header: FC<HeaderProps> = () => {
  return (
    <div className="header-section">
      <div>
        <img src={logo} alt="logo" />
      </div>
      <div className="nav-buttons">
        {headerLinks.map((button: headerLinksProps) => {
          return (
            <NavLink
              to={button.route}
              className={({ isActive }) =>
                isActive ? "nav-block-active" : undefined
              }
              key={button.id}
            >
              <div className="links">
                <li className="button-text">{button.name}</li>
              </div>
            </NavLink>
          );
        })}
      </div>
    </div>
  );
};

export default Header;
