import { FC, useState } from "react";
import "../../styles/tabs.scss";
import TabsButtons from "./TabsButtons";

interface TabsProps {}

interface tabsDataProps {
  id: string;
  name: string;
  content: string;
}

const tabButtons: tabsDataProps[] = [
  {
    id: "1",
    name: "Kreće treća sezona podcasta u zoni!",
    content: "",
  },
  {
    id: "2",
    name: "Serija radionica u Klubu Zona",
    content: "",
  },
  {
    id: "3",
    name: "Prijavi se na Split Shapers Day!",
    content: "",
  },
  {
    id: "4",
    name: "Podcasti u Zoni",
    content: "",
  },
  {
    id: "5",
    name: "Uđi u Građanski inkubator",
    content: "",
  },
];

const Tabs: FC<TabsProps> = () => {
  const [selected, setSelected] = useState<number>(0);
  // const [slide, setSlide] = useState<number>(0);
  // const tabsLength = tabData.length;

  // const nextTab = () => {
  //   const next = slide === tabsLength - 1 ? 0 : slide + 1;
  //   setSlide(next);
  // };
  // const prevTab = () => {
  //   const prev = slide === 0 ? tabsLength - 1 : slide - 1;
  //   setSlide(prev);
  // };
  // console.log("slide", slide);

  const handleSelect = (id: number) => {
    const buttons = tabButtons.map((item) => parseInt(item.id) === id);
    setSelected(id);
    return buttons;
  };

  console.log("selected", selected);

  return (
    <div>
      <TabsButtons
        tabButtons={tabButtons}
        handleSelect={handleSelect}
        selected={selected}
      ></TabsButtons>
      <hr className="rule" />
    </div>
  );
};

export default Tabs;
