import { FC } from "react";
import "../../styles/tabsButtons.scss";

interface TabsButtonsProps {
  tabButtons: {
    id: string;
    name: string;
    content: string;
  }[];
  handleSelect: any;
  selected: number;
}

const TabsButtons: FC<TabsButtonsProps> = ({
  tabButtons,
  handleSelect,
  selected,
}) => {
  return (
    <div>
      <div></div>
      <div className="tab-section">
        {tabButtons.map((button: { id: string; name: string }) => {
          return (
            <div
              className={selected === parseInt(button.id) ? "tab-active" : "tab"}
              key={button.id}
              onClick={() => handleSelect(parseInt(button.id))}
            >
              <p className="tab-text">{button.name}</p>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default TabsButtons;
