import { FC, useCallback, useEffect, useState } from "react";
import { toast } from "react-toastify";

interface BannerProps {}

const Banner: FC<BannerProps> = () => {
  const [data, setData] = useState<[]>([]);

  const url: string = `http://localhost:5000/api/topHeadlines`;

  const getTopHeadlines = useCallback(async () => {
    try {
      const request = await fetch(url, {
        method: "GET",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
          "Access-Control-Allow-Origin": "*",
        },
      });
      const response = await request.json();
      if (response.status === "error") {
        toast.error(response.code, {
          position: "top-right",
          autoClose: false,
          hideProgressBar: true,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          theme: "colored",
        });
      } else {
        setData(response.articles);
        console.log("response", response);
      }
    } catch (error) {
      console.log(error);
    }
  }, [url]);

  useEffect(() => {
    getTopHeadlines();
  }, [getTopHeadlines]);

  return (
    <div>
      {data.map((item: any) => {
        return (
          <div key={item.publishedAt}>
            <div className="header-section">
              <h3>{item.title}</h3>
              <div>
                <button>Kategorija</button>
                <button>Vrijeme ƒçitanja 5 min</button>
              </div>
              <div>
                <img src={item.urlToImage} alt={item.description} />
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default Banner;
