    }
  };

  const parseTime = (time: string) => {
    //  5 / 4 / 2023;
    //  5 / 18 / 2023;
    const parsedDate = new Date(time).toLocaleDateString();
    const day = parsedDate[0].split("/");
    const month = parsedDate.slice(0, 3);
    console.log("day", day);
    console.log("month", month);
    const parsedTime = new Date(time).toLocaleTimeString();
    return `${parsedDate} ${parsedTime}`;
  };

  return (
    <div>
      <div className="latest">
        <div className="latest-section" onScroll={handleScroll} ref={refEl}>
          <div className="top">
            <h4 className="title">Latest news</h4>
          </div>
          <div>
            {data?.map((item: ArticleProps, index: number) => {
              return (
                <div className="latest-content" key={index}>
                  <p className="time">
                    Published: {parseTime(item.publishedAt)}
                  </p>
                  <p className="text">{item?.description}</p>
                </div>
              );
            })}
          </div>
        </div>
        <div className="bottom">
          <button className="btn-see-all">
            See all news <span className="arrow">{">"}</span>
          </button>
        </div>
      </div>
    </div>
  );
};

export default InfiniteNews;
