import React, { FC, useCallback, useEffect, useState } from "react";
import { toast } from "react-toastify";

interface HighOrderCompProps {
  Component: React.FC | FC<{ data: any[] }>;
}

const HighOrderComp: FC<HighOrderCompProps> = (
  Component: HighOrderCompProps
) => {
  const HOC = () => {
    const url: string = `http://localhost:5000/api/topHeadlines`;
    const [data, setData] = useState<[]>([]);

    const getTopHeadlines = useCallback(async () => {
      try {
        const request = await fetch(url, {
          method: "GET",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
            "Access-Control-Allow-Origin": "*",
          },
        });
        const response = await request.json();
        if (response.status === "error") {
          toast.error(response.code, {
            position: "top-right",
            autoClose: false,
            hideProgressBar: true,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: "colored",
          });
        } else {
          setData(response.articles);
          console.log("response", response);
        }
      } catch (error: any) {
        toast.error(error.message, {
          position: "top-right",
          autoClose: false,
          hideProgressBar: true,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          theme: "colored",
        });
        console.log(error);
      }
    }, [url]);

    useEffect(() => {
      getTopHeadlines();
    }, [getTopHeadlines]);

    return (
      <div>
        <Component data={data}></Component>
      </div>
    );
  };
  return HOC;
};

export default HighOrderComp;
