import { FC, useState } from "react";
import "../../styles/tabs.scss";

interface TabsProps {}

interface tabsDataProps {
  id: string;
  name: string;
  content: string;
}

const tabData: tabsDataProps[] = [
  {
    id: "1",
    name: "Kreće treća sezona podcasta u zoni!",
    content: "",
  },
  {
    id: "2",
    name: "Serija radionica u Klubu Zona",
    content: "",
  },
  {
    id: "3",
    name: "Prijavi se na Split Shapers Day!",
    content: "",
  },
  {
    id: "4",
    name: "Podcasti u Zoni",
    content: "",
  },
  {
    id: "5",
    name: "Uđi u Građanski inkubator",
    content: "",
  },
];

const Tabs: FC<TabsProps> = () => {
  const [slide, setSlide] = useState<number>(0);
  const tabsLength = tabData.length;
  const buttons = [{ id: "1", }];

  const nextTab = () => {
    const next = slide === tabsLength - 1 ? 0 : slide + 1;
    setSlide(next);
  };
  const prevTab = () => {
    const prev = slide === 0 ? tabsLength - 1 : slide - 1;
    setSlide(prev);
  };
  console.log("slide", slide);

  return (
    <div>
      <div className="tab-section">
        {tabData.map((tab: tabsDataProps) => {
          return (
            <div key={tab.id} className="tab">
              <p className="tab-text">{tab.name}</p>
            </div>
          );
        })}
      </div>
      <hr className="rule" />
    </div>
  );
};

export default Tabs;
