import { FC, useCallback, useEffect, useState } from "react";
import mockImage from "../../assets/images/mockImage.jpg";
import { Link } from "react-router-dom";
import { toast } from "react-toastify";
import "../../styles/banner.scss";

interface BannerProps {}

const Banner: FC<BannerProps> = () => {
  const [data, setData] = useState<[]>([]);

  const url: string = `http://localhost:5000/api/topHeadlines`;

  const getTopHeadlines = useCallback(async () => {
    try {
      const request = await fetch(url, {
        method: "GET",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
          "Access-Control-Allow-Origin": "*",
        },
      });
      const response = await request.json();
      if (response.status === "error") {
        toast.error(response.code, {
          position: "top-right",
          autoClose: false,
          hideProgressBar: true,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          theme: "colored",
        });
      } else {
        setData(response.articles);
        console.log("response", response);
      }
    } catch (error: unknown) {
      toast.error(errror.message, {
        position: "top-right",
        autoClose: false,
        hideProgressBar: true,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "colored",
      });
      console.log(error);
    }
  }, [url]);

  useEffect(() => {
    getTopHeadlines();
  }, [getTopHeadlines]);

  const parseTime = (time: string) => {
    const parsedDate = new Date(time).toLocaleDateString();
    const parsedTime = new Date(time).toLocaleTimeString();
    return `${parsedDate} ${parsedTime}`;
  };

  return (
    <>
      <div>
        {data.slice(1, 2).map((item: any) => {
          return (
            <div key={item.publishedAt} className="header-section">
              <div className="header-left">
                <h3 className="title">{item.title}</h3>
                <div>
                  <Link className="category-btn" to={item.url}>
                    Kategorija
                  </Link>
                  <Link className="category-btn" to={item.url}>
                    Vrijeme ƒçitanja 5 min
                  </Link>
                  <p className="time">
                    Published: {parseTime(item.publishedAt)}
                  </p>
                </div>
              </div>
              <div className="header-right">
                {item.urlToImage !== undefined || item.urlToImage !== null ? (
                  <img
                    width={650}
                    height={400}
                    loading="lazy"
                    src={item.urlToImage}
                    alt={item.description}
                  />
                ) : (
                  <img
                    width={650}
                    height={400}
                    loading="lazy"
                    src={mockImage}
                    alt={item.description}
                  />
                )}
              </div>
            </div>
          );
        })}
        <hr />
      </div>
      <div className="middle-section">
        {data.slice(2, 5).map((item: any) => {
          return (
            <div className="card" key={item.publishedAt}>
              {item.urlToImage === undefined || item.urlToImage === null ? (
                <img
                  width={350}
                  height={222}
                  loading="lazy"
                  src={mockImage}
                  alt={item.description}
                />
              ) : (
                <img
                  width={350}
                  height={222}
                  loading="lazy"
                  src={item.urlToImage}
                  alt={item.description}
                />
              )}
              <p className="time">Published: {parseTime(item.publishedAt)}</p>
              <h3 className="title">{item.title}</h3>
              <Link className="category-btn" to={item.url}>
                Enter
              </Link>
            </div>
          );
        })}
      </div>
      <hr />
      <div className="bottom-section">
        <div className="column">
          {data.slice(5, 10).map((item: any) => {
            return (
              <div className="card-bottom" key={item.publishedAt}>
                <div>
                  {item.urlToImage === undefined || item.urlToImage === null ? (
                    <img
                      width={200}
                      height={122}
                      loading="lazy"
                      src={mockImage}
                      alt={item.description}
                    />
                  ) : (
                    <img
                      width={200}
                      height={122}
                      loading="lazy"
                      src={item.urlToImage}
                      alt={item.description}
                    />
                  )}
                </div>
                <div className="right-side">
                  <p className="time">
                    Published: {parseTime(item.publishedAt)}
                  </p>
                  <h3 className="title">{item.title}</h3>
                  <Link className="category-btn" to={item.url}>
                    Enter
                  </Link>
                </div>
              </div>
            );
          })}
        </div>
        <div>
          <p>Left side</p>
        </div>
      </div>
      <hr className="rule" />
      {/* 2 div */}
    </>
  );
};

export default Banner;
