import { FC, useRef } from "react";
import "../../styles/footer.scss";
import fb from "../../assets/images/fb.svg";
import twitter from "../../assets/images/twitter.svg";
import play from "../../assets/images/play.svg";
import google from "../../assets/images/google.svg";

interface FooterProps {}

interface footerDataProps {
  id: string;
  img: string;
  name: string;
  color: string;
}

interface footerLinksProps {
  id: string;
  name: string;
  link: string;
}

const footerData: footerDataProps[] = [
  {
    id: "1",
    img: fb,
    name: "facebook",
    color: "blue",
  },
  {
    id: "2",
    img: twitter,
    name: "twitter",
    color: "red",
  },
  {
    id: "3",
    img: play,
    name: "play",
    color: "blue",
  },
  {
    id: "4",
    img: google,
    name: "google",
    color: "blue",
  },
];

const footerLinks = [
  {
    id: "1",
    name: "Impressum",
    link: "/",
  },
  {
    id: "2",
    name: "Pravila korištenja",
    link: "/",
  },
  {
    id: "3",
    name: "Kontakt",
    link: "/",
  },
];

const Footer: FC<FooterProps> = () => {
  const buttonRef = useRef<HTMLButtonElement[]>([]);

  const handleMouseEnter = (index: number, color: any) => {
    if (buttonRef.current[index]) {
      buttonRef.current[index].style.backgroundColor = "blue";
    }
  };

  const handleMouseLeave = () => {
    buttonRef.current.style.backgroundColor = "white";
  };
  const date: number = new Date().getFullYear();

  return (
    <div className="footer-section">
      <div className="icon-section">
        {footerData.map((item: footerDataProps) => {
          return (
            <div className="icons" key={item.id}>
              <span
                className="rounded"
                ref={buttonRef}
                onMouseEnter={(e) => handleMouseEnter(item.color)}
                onMouseLeave={handleMouseLeave}
              >
                <img className="img-icon" src={item.img} alt={item.name} />
              </span>
            </div>
          );
        })}
      </div>
      <div className="footer-links">
        <p className="date">© {date} infozona.hr</p>
        {footerLinks.map((item: footerLinksProps) => {
          return (
            <div className="links-section" key={item.id}>
              <p className="links-text">{item.name}</p>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Footer;
