import { FC, useCallback, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { toast } from "react-toastify";
import "../../styles/banner.scss";

interface BannerProps {}

const Banner: FC<BannerProps> = () => {
  const [data, setData] = useState<[]>([]);

  const url: string = `http://localhost:5000/api/topHeadlines`;

  const getTopHeadlines = useCallback(async () => {
    try {
      const request = await fetch(url, {
        method: "GET",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
          "Access-Control-Allow-Origin": "*",
        },
      });
      const response = await request.json();
      if (response.status === "error") {
        toast.error(response.code, {
          position: "top-right",
          autoClose: false,
          hideProgressBar: true,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          theme: "colored",
        });
      } else {
        setData(response.articles);
        console.log("response", response);
      }
    } catch (error: unknown) {
      console.log(error);
    }
  }, [url]);

  useEffect(() => {
    getTopHeadlines();
  }, [getTopHeadlines]);

  return (
    <>
      <div>
        {data.slice(0, 1).map((item: any) => {
          return (
            <div key={item.publishedAt} className="header-section">
              <div className="header-left">
                <h3 className="title">{item.title}</h3>
                <div>
                  <Link className="category-btn" to={item.url}>
                    Kategorija
                  </Link>
                  <Link className="category-btn" to={item.url}>
                    Vrijeme ƒçitanja 5 min
                  </Link>
                </div>
              </div>
              <div className="header-right">
                {item.urlToImage !== undefined || item.urlToImage !== null ? (
                  <img
                    width={650}
                    height={400}
                    loading="lazy"
                    src={item.urlToImage}
                    alt={item.description}
                  />
                ) : (
                  <img
                    width={650}
                    height={400}
                    loading="lazy"
                    src={item[1].urlToImage}
                    alt={item.description}
                  />
                )}
              </div>
            </div>
          );
        })}
        <hr />
      </div>
      <div className="middle-section">
        {data.slice(2, 5).map((item: any) => {
          return (
            <div className="card">
              <img
                width={350}
                height={222}
                loading="lazy"
                src={item.urlToImage}
                alt={item.description}
              />
              <h3 className="title">{item.title}</h3>
              <Link className="category-btn" to={item.url}>
                Enter
              </Link>
            </div>
          );
        })}
      </div>
      {/* 2 div */}
    </>
  );
};

export default Banner;
